contracts/facets/MerkleFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "../libraries/LibMerkleStorage.sol";
import "../libraries/LibAppStorage.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";

contract MerkleFacet {
    event MerkleRootSubmitted(uint256 indexed quizId, bytes32 root);

    function _appStorage() internal pure returns (LibAppStorage.AppStorage storage ds) {
        ds = LibAppStorage.s();
    }

    modifier onlyAdmin() {
        LibAppStorage.AppStorage storage ds = _appStorage();
        require(
            AccessControlUpgradeable(address(this)).hasRole(ds.DEFAULT_ADMIN_ROLE, msg.sender),
            "MerkleFacet: Caller is not an admin"
        );
        _;
    }

    /// @notice Commit Merkle root for a quiz (store only root)
    function submitMerkleRoot(uint256 quizId, bytes32 root) external onlyAdmin {
        LibMerkleStorage.MerkleStorage storage ms = LibMerkleStorage.merkleStorage();
        ms.quizRoots[quizId] = root;

        emit MerkleRootSubmitted(quizId, root);
    }

    /// @notice Verify leaf against stored Merkle root
    function verifyQuiz(
        uint256 quizId,
        bytes32 leaf,
        bytes32[] calldata proof
    ) external view returns (bool) {
        LibMerkleStorage.MerkleStorage storage ms = LibMerkleStorage.merkleStorage();
        bytes32 root = ms.quizRoots[quizId];
        require(root != 0, "MerkleFacet: Root not found");
        return MerkleProof.verify(proof, root, leaf);
    }

    /// @notice Get Merkle root for a quizId
    function getMerkleRoot(uint256 quizId) external view returns (bytes32) {
        return LibMerkleStorage.merkleStorage().quizRoots[quizId];
    }
}

contracts/facets/QuizCreationFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import {LibDiamond} from "../libraries/LibDiamond.sol";
import {LibQuizStorage} from "../libraries/LibQuizStorage.sol";
import {IERC173} from "../interfaces/IERC173.sol";
import {Quiz, Question} from "../QuizTypes.sol";

// Interface for Ownership
interface IOwnership {
    function owner() external view returns (address contractOwner);
}

// --- Events for QuizCreation ---
event QuizCreated(uint256 indexed quizId, string name, address indexed creator, uint256 rewardAmount);
event QuestionAdded(uint256 indexed quizId, uint256 indexed questionIndex);
event QuizActivated(uint256 indexed quizId);
event QuizDeactivated(uint256 indexed quizId);


// --- QuizCreationFacet Contract ---
contract QuizCreationFacet {
    function _diamondStorage() internal pure returns (LibDiamond.DiamondStorage storage ds) {
        ds = LibDiamond.diamondStorage();
    }

    function _quizStorage() internal pure returns (LibQuizStorage.QuizStorage storage qs) {
        qs = LibQuizStorage.quizStorage();
    }

    // Modifier to check if the caller is the Diamond owner
    modifier onlyOwner() {
        require(IOwnership(address(this)).owner() == msg.sender, "QuizCreationFacet: Must be owner");
        _;
    }

    /// @notice Creates a new Quiz
    /// @param _name The name of the Quiz
    /// @param _rewardAmount The reward amount per correct answer (in wei)
    /// @param _maxParticipants The maximum number of participants
    /// @return quizId The ID of the created Quiz
    function createQuiz(string memory _name, uint256 _rewardAmount, uint256 _maxParticipants)
        external
        onlyOwner
        returns (uint256 quizId)
    {
        LibQuizStorage.QuizStorage storage qs = _quizStorage();
        quizId = qs.nextQuizId;

        Quiz storage newQuiz = qs.quizzes[quizId];
        newQuiz.name = _name;
        newQuiz.creator = msg.sender;
        newQuiz.rewardAmount = _rewardAmount;
        newQuiz.totalQuestions = 0;
        newQuiz.maxParticipants = _maxParticipants;
        newQuiz.isActive = false;

        qs.nextQuizId++;

        emit QuizCreated(quizId, _name, msg.sender, _rewardAmount);
        return quizId;
    }

    /// @notice Adds a question to a Quiz
    /// @param _quizId The ID of the Quiz to add the question to
    /// @param _questionText The question text
    /// @param _options The answer options
    /// @param _correctAnswerIndex The index of the correct answer (starting from 0)
    function addQuestion(uint256 _quizId, string memory _questionText, string[] memory _options, uint8 _correctAnswerIndex)
        external
        onlyOwner
    {
        LibQuizStorage.QuizStorage storage qs = _quizStorage();
        Quiz storage quiz = qs.quizzes[_quizId];

        require(quiz.creator != address(0), "QuizCreationFacet: Quiz does not exist");
        require(quiz.creator == msg.sender, "QuizCreationFacet: Must be quiz creator to add questions");
        require(!quiz.isActive, "QuizCreationFacet: Cannot add questions to an active quiz");

        require(_options.length >= 2, "QuizCreationFacet: Must provide at least two options");
        require(_correctAnswerIndex < _options.length, "QuizCreationFacet: Invalid correct answer index");

        uint256 questionIndex = quiz.totalQuestions;
        
        Question storage newQuestion = qs.quizQuestions[_quizId][questionIndex];
        newQuestion.questionText = _questionText;
        newQuestion.options = _options;
        newQuestion.correctAnswerIndex = _correctAnswerIndex;
        
        quiz.totalQuestions++;

        emit QuestionAdded(_quizId, questionIndex);
    }

    /// @notice Activates a Quiz
    /// @param _quizId The ID of the Quiz to activate
    function activateQuiz(uint256 _quizId) external onlyOwner {
        LibQuizStorage.QuizStorage storage qs = _quizStorage();
        Quiz storage quiz = qs.quizzes[_quizId];

        require(quiz.creator != address(0), "QuizCreationFacet: Quiz does not exist");
        require(quiz.creator == msg.sender, "QuizCreationFacet: Must be quiz creator to activate");
        require(quiz.totalQuestions > 0, "QuizCreationFacet: Cannot activate a quiz with no questions");
        require(!quiz.isActive, "QuizCreationFacet: Quiz is already active");

        quiz.isActive = true;
        emit QuizActivated(_quizId);
    }

    /// @notice Deactivates a Quiz
    /// @param _quizId The ID of the Quiz to deactivate
    function deactivateQuiz(uint256 _quizId) external onlyOwner {
        LibQuizStorage.QuizStorage storage qs = _quizStorage();
        Quiz storage quiz = qs.quizzes[_quizId];

        require(quiz.creator != address(0), "QuizCreationFacet: Quiz does not exist");
        require(quiz.creator == msg.sender, "QuizCreationFacet: Must be quiz creator to deactivate");
        require(quiz.isActive, "QuizCreationFacet: Quiz is not active");

        quiz.isActive = false;
        emit QuizDeactivated(_quizId);
    }

    /// @notice Retrieves Quiz data
    /// @param _quizId The ID of the Quiz
    /// @return name The name of the quiz
    /// @return creator The address of the quiz creator
    /// @return rewardAmount The reward amount for the quiz
    /// @return totalQuestions The total number of questions in the quiz
    /// @return maxParticipants The maximum number of participants allowed
    /// @return isActive The active status of the quiz
    function getQuiz(uint256 _quizId) external view returns (
        string memory name,
        address creator,
        uint256 rewardAmount,
        uint256 totalQuestions,
        uint256 maxParticipants,
        bool isActive
    ) {
        LibQuizStorage.QuizStorage storage qs = _quizStorage();
        Quiz storage quiz = qs.quizzes[_quizId];
        require(quiz.creator != address(0), "QuizCreationFacet: Quiz does not exist");
        
        return (
            quiz.name,
            quiz.creator,
            quiz.rewardAmount,
            quiz.totalQuestions,
            quiz.maxParticipants,
            quiz.isActive
        );
    }

    /// @notice Retrieves specific question data
    /// @param _quizId The ID of the Quiz
    /// @param _questionIndex The index of the question
    /// @return questionText The text of the question
    /// @return options The options for the question
    /// @return correctAnswerIndex The index of the correct answer
    function getQuestion(uint256 _quizId, uint256 _questionIndex) external view returns (
        string memory questionText,
        string[] memory options,
        uint8 correctAnswerIndex
    ) {
        LibQuizStorage.QuizStorage storage qs = _quizStorage();
        Quiz storage quiz = qs.quizzes[_quizId];
        require(quiz.creator != address(0), "QuizCreationFacet: Quiz does not exist");
        require(_questionIndex < quiz.totalQuestions, "QuizCreationFacet: Invalid question index");
        
        Question storage question = qs.quizQuestions[_quizId][_questionIndex];
        
        return (
            question.questionText,
            question.options,
            question.correctAnswerIndex
        );
    }

    /// @notice Retrieves the total number of Quizzes created
    /// @return totalQuizzes The total number of Quizzes
    function getTotalQuizzes() external view returns (uint256 totalQuizzes) {
        LibQuizStorage.QuizStorage storage qs = _quizStorage();
        return qs.nextQuizId;
    }
}

contracts/facets/QuizGameBaseFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { LibAppStorage } from '../libraries/LibAppStorage.sol';
import { AccessControlUpgradeable } from '@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol';
import '../interfaces/IPoolManager.sol';
import '../interfaces/IQuizCoin.sol';
import '../interfaces/IQuizGameEvents.sol';
import { Question } from '../QuizTypes.sol'; // อ้างอิงถึง Question struct โดยตรง

event PoolManagerAddressUpdated(address oldAddress, address newAddress);
event QuizCoinAddressUpdated(address oldAddress, address newAddress);

contract QuizGameBaseFacet is IQuizGameEvents {
    function initializeQuizGame() public {
        LibAppStorage.AppStorage storage ds = LibAppStorage.s();

        require(ds.GAME_START_TIMESTAMP == 0, "QuizGame: Already initialized");

        ds.DEFAULT_ADMIN_ROLE = AccessControlUpgradeable(address(this)).DEFAULT_ADMIN_ROLE();
        ds.REWARD_DISTRIBUTOR_ROLE = keccak256("REWARD_DISTRIBUTOR_ROLE");
        ds.CREATOR_ROLE = keccak256("CREATOR_ROLE");

        ds.HINT_COST_AMOUNT = 10 * 10**18;
        ds.POOL_REWARD_WINDOW_DURATION_SECONDS = 180;
        ds.LEVEL_100_QUESTION_VALIDITY_SECONDS = 24 * 60 * 60;
        ds.REWARD_FOR_LEVEL_1_99 = 5000 * 10**18;
        ds.REWARD_FOR_LEVEL_100 = 10000 * 10**18;
        ds.HALVING_PERIOD_SECONDS = 4 * 365 * 24 * 60 * 60;
        ds.MIN_REWARD_AFTER_HALVING = 100 * 10**18;
        ds.TREASURY_FEE_PERCENTAGE = 50;
        ds.nextQuestionId = 1;
        ds.GAME_START_TIMESTAMP = block.timestamp;
    }

    // --- Admin Functions for setting external contract addresses ---
    function setPoolManagerAddress(address _newPoolManagerAddress) public {
        LibAppStorage.AppStorage storage ds = LibAppStorage.s();
        require(AccessControlUpgradeable(address(this)).hasRole(ds.DEFAULT_ADMIN_ROLE, msg.sender), "AccessControl: Caller is not a game admin");
        require(_newPoolManagerAddress != address(0), "Quiz: PoolManager address cannot be zero");
        emit PoolManagerAddressUpdated(address(ds.poolManager), _newPoolManagerAddress);
        ds.poolManager = IPoolManager(_newPoolManagerAddress);
    }

    function setQuizCoinAddress(address _newQuizCoinAddress) public {
        LibAppStorage.AppStorage storage ds = LibAppStorage.s();
        require(AccessControlUpgradeable(address(this)).hasRole(ds.DEFAULT_ADMIN_ROLE, msg.sender), "AccessControl: Caller is not a game admin");
        require(_newQuizCoinAddress != address(0), "Quiz: QuizCoin address cannot be zero");
        emit QuizCoinAddressUpdated(address(ds.quizCoin), _newQuizCoinAddress);
        ds.quizCoin = IQuizCoin(_newQuizCoinAddress);
    }

    // --- Public Getters for AppStorage Constants and Addresses ---
    function getPoolManagerAddress() public view returns (address) {
        LibAppStorage.AppStorage storage ds = LibAppStorage.s();
        return address(ds.poolManager);
    }

    function getQuizCoinAddress() public view returns (address) {
        LibAppStorage.AppStorage storage ds = LibAppStorage.s();
        return address(ds.quizCoin);
    }

    function DEFAULT_ADMIN_ROLE() public view returns (bytes32) {
        LibAppStorage.AppStorage storage ds = LibAppStorage.s();
        return ds.DEFAULT_ADMIN_ROLE;
    }
    function REWARD_DISTRIBUTOR_ROLE() public view returns (bytes32) {
        LibAppStorage.AppStorage storage ds = LibAppStorage.s();
        return ds.REWARD_DISTRIBUTOR_ROLE;
    }
    function CREATOR_ROLE() public view returns (bytes32) {
        LibAppStorage.AppStorage storage ds = LibAppStorage.s();
        return ds.CREATOR_ROLE;
    }

    function hasRole(bytes32 role, address account) public view returns (bool) {
        return AccessControlUpgradeable(address(this)).hasRole(role, account);
    }

    function getPoolRewardWindowDuration() public view returns (uint256) {
        return LibAppStorage.s().POOL_REWARD_WINDOW_DURATION_SECONDS;
    }
    function getLevel100QuestionValiditySeconds() public view returns (uint256) {
        return LibAppStorage.s().LEVEL_100_QUESTION_VALIDITY_SECONDS;
    }
    function getBaseRewardForLevel99() public view returns (uint256) {
        return LibAppStorage.s().REWARD_FOR_LEVEL_1_99;
    }
    function getRewardForLevel100() public view returns (uint256) {
        return LibAppStorage.s().REWARD_FOR_LEVEL_100;
    }
    function getTreasuryFeePercentage() public view returns (uint256) {
        return LibAppStorage.s().TREASURY_FEE_PERCENTAGE;
    }
    function getHalvingPeriodSeconds() public view returns (uint256) {
        return LibAppStorage.s().HALVING_PERIOD_SECONDS;
    }
    function getMinRewardAfterHalving() public view returns (uint256) {
        return LibAppStorage.s().MIN_REWARD_AFTER_HALVING;
    }
    function getGameStartTimestamp() public view returns (uint256) {
        return LibAppStorage.s().GAME_START_TIMESTAMP;
    }
    function getNextQuestionId() public view returns (uint256) {
        return LibAppStorage.s().nextQuestionId;
    }
    
    // แก้ไข: ใช้ Question struct ที่นำเข้าโดยตรง
    function getQuestion(uint256 _questionId) public view returns (
        bytes32 correctAnswerHash,
        bytes32 hintHash,
        address questionCreator,
        uint256 difficultyLevel,
        uint256 baseRewardAmount,
        bool isClosed,
        LibAppStorage.QuestionMode mode,
        uint256 blockCreationTime,
        uint256 firstCorrectAnswerTime,
        address firstSolverAddress,
        address[] memory poolCorrectSolvers
    ) {
        LibAppStorage.AppStorage storage ds = LibAppStorage.s();
        // แก้ไขบรรทัดนี้: ลบ LibAppStorage. ออก
        Question storage question = ds.questions[_questionId];
        return (
            question.correctAnswerHash,
            question.hintHash,
            question.questionCreator,
            question.difficultyLevel,
            question.baseRewardAmount,
            question.isClosed,
            question.mode,
            question.blockCreationTime,
            question.firstCorrectAnswerTime,
            question.firstSolverAddress,
            question.poolCorrectSolvers
        );
    }
}

contracts/facets/QuizGameModeFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { LibAppStorage } from '../libraries/LibAppStorage.sol';
import { AccessControlUpgradeable } from '@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol';
import { IPoolManager } from '../interfaces/IPoolManager.sol';
import { IQuizGameEvents } from '../interfaces/IQuizGameEvents.sol';
import { IQuizCoin } from '../interfaces/IQuizCoin.sol';
import { IQuizGameReward } from '../interfaces/IQuizGameReward.sol';
import { Question } from '../QuizTypes.sol';
import { IMerkleFacet } from "../interfaces/IMerkleFacet.sol";

contract QuizGameModeFacet is IQuizGameEvents {

    function _appStorage() internal pure returns (LibAppStorage.AppStorage storage ds) {
        ds = LibAppStorage.s();
    }

    modifier onlyAdmin() {
        LibAppStorage.AppStorage storage ds = _appStorage();
        require(
            AccessControlUpgradeable(address(this)).hasRole(ds.DEFAULT_ADMIN_ROLE, msg.sender),
            "AccessControl: Caller is not a game admin"
        );
        _;
    }

    modifier onlyAdminOrDistributor() {
        LibAppStorage.AppStorage storage ds = _appStorage();
        require(
            AccessControlUpgradeable(address(this)).hasRole(ds.DEFAULT_ADMIN_ROLE, msg.sender) ||
            AccessControlUpgradeable(address(this)).hasRole(ds.REWARD_DISTRIBUTOR_ROLE, msg.sender),
            "AccessControl: Caller is not an admin or reward distributor"
        );
        _;
    }

    /// @notice สร้างคำถามใหม่และบันทึกข้อมูลบน Smart Contract
    function createQuestion(
        bytes32 _answerLeaf, // leaf ของคำตอบที่จะไปอยู่ใน Merkle Tree
        bytes32 _hintHash,
        uint256 _difficultyLevel,
        LibAppStorage.QuestionMode _mode
    ) public onlyAdmin {
        LibAppStorage.AppStorage storage ds = _appStorage();

        require(_answerLeaf != bytes32(0), "Quiz: Answer leaf cannot be zero");
        require(_difficultyLevel >= 1 && _difficultyLevel <= 100, "Quiz: Difficulty must be 1-100");

        uint256 questionId = ds.nextQuestionId;
        ds.nextQuestionId++;

        uint256 calculatedBaseReward;
        if (_difficultyLevel == 100) {
            calculatedBaseReward = ds.REWARD_FOR_LEVEL_100;
        } else {
            calculatedBaseReward = (ds.REWARD_FOR_LEVEL_1_99 * _difficultyLevel) / 99;
        }

        Question storage newQuestion = ds.questions[questionId];
        newQuestion.correctAnswerHash = _answerLeaf; // เก็บ leaf ที่ backend สร้าง
        newQuestion.hintHash = _hintHash;
        newQuestion.questionCreator = msg.sender;
        newQuestion.difficultyLevel = _difficultyLevel;
        newQuestion.baseRewardAmount = calculatedBaseReward;
        newQuestion.isClosed = false;
        newQuestion.mode = _mode;
        newQuestion.blockCreationTime = block.timestamp;
        newQuestion.firstCorrectAnswerTime = 0;
        newQuestion.firstSolverAddress = address(0);
        newQuestion.poolCorrectSolvers = new address [](0);

        emit QuestionCreated(questionId, msg.sender, _difficultyLevel, calculatedBaseReward);
    }

    /// @notice 🚪 NEW: ผู้เล่นส่งคำตอบ + Merkle proof (Leaf-Level Door System)
    function submitAnswer(
        uint256 _questionId,
        bytes32 _answerLeaf,
        bytes32[] calldata _merkleProof
    ) public {
        LibAppStorage.AppStorage storage ds = _appStorage();
        Question storage question = ds.questions[_questionId];

        // Basic validations
        require(question.correctAnswerHash != bytes32(0), "Quiz: Question does not exist.");
        
        // 🚪 NEW: Check individual leaf door instead of main door
        require(!ds.leafSolved[_answerLeaf], "Quiz: This specific quiz already solved.");

        // Level 100 expiry check
        if (question.difficultyLevel == 100) {
            require(block.timestamp <= question.blockCreationTime + ds.LEVEL_100_QUESTION_VALIDITY_SECONDS,
                "Quiz: Level 100 expired.");
        }

        // ✅ Verify with Merkle proof
        bool isCorrect = IMerkleFacet(address(this)).verifyQuiz(_questionId, _answerLeaf, _merkleProof);
        require(isCorrect, "Quiz: Wrong answer or invalid proof");

        // Daily play restrictions (keep existing logic)
        uint256 currentDayId = block.timestamp / (24 * 60 * 60);
        if (ds.lastPlayedDay[msg.sender] != currentDayId) {
            ds.lastPlayedDay[msg.sender] = currentDayId;
            ds.playerModeChoice[msg.sender] = question.mode;
        } else {
            require(ds.playerModeChoice[msg.sender] == question.mode,
                "Quiz: Already chosen different mode today.");
        }

        require(question.lastAnswerDay[msg.sender] != currentDayId,
            "Quiz: Can only answer once per day.");
        question.lastAnswerDay[msg.sender] = currentDayId;

        // 🚪 NEW: Close THIS leaf's door only
        ds.leafSolved[_answerLeaf] = true;
        ds.leafSolver[_answerLeaf] = msg.sender;
        ds.leafSolveTime[_answerLeaf] = block.timestamp;
        ds.leafQuestionId[_answerLeaf] = _questionId;

        // Calculate and distribute reward for this specific leaf
        uint256 totalReward = IQuizGameReward(address(this)).calculateCurrentReward(question.difficultyLevel);
        uint256 treasuryFee = (totalReward * ds.TREASURY_FEE_PERCENTAGE) / 10000;
        uint256 rewardForSolver = totalReward - treasuryFee;

        ds.poolManager.mintAndTransferToTreasury(treasuryFee);
        ds.poolManager.withdrawForUser(msg.sender, rewardForSolver);

        // Emit new leaf-specific events
        emit LeafSolved(_questionId, _answerLeaf, msg.sender, rewardForSolver);
        emit AnswerSubmitted(_questionId, msg.sender, _answerLeaf, isCorrect);
    }
        function buyHint(uint256 _questionId) public {
        LibAppStorage.AppStorage storage ds = LibAppStorage.s();
        
        Question storage question = ds.questions[_questionId];
        require(question.correctAnswerHash != bytes32(0), "Quiz: Question does not exist.");
        require(!question.isClosed, "Quiz: Question is already closed.");

        if (question.difficultyLevel == 100) {
            require(block.timestamp <= question.blockCreationTime + ds.LEVEL_100_QUESTION_VALIDITY_SECONDS, "Quiz: Level 100 question has expired, cannot buy hint.");
        }

        require(ds.quizCoin.transferFrom(msg.sender, address(this), ds.HINT_COST_AMOUNT), "Quiz: QuizCoin transfer failed for hint.");

        emit HintPurchased(_questionId, msg.sender, ds.HINT_COST_AMOUNT);
    }

    // --- เพิ่มฟังก์ชันสำหรับ Question Bank ---
    function activateQuestionFromBank(uint256 _questionId) public {
        LibAppStorage.AppStorage storage ds = LibAppStorage.s();
        require(
            AccessControlUpgradeable(address(this)).hasRole(ds.DEFAULT_ADMIN_ROLE, msg.sender) ||
            AccessControlUpgradeable(address(this)).hasRole(ds.CREATOR_ROLE, msg.sender),
            "AccessControl: Caller is not a game admin or creator"
        );

        Question storage question = ds.questions[_questionId];
        require(question.correctAnswerHash != bytes32(0), "Quiz: Question does not exist in bank.");
        
        // Reset สถานะคำถามเพื่อเปิดใช้งานใหม่
        question.isClosed = false;
        question.firstCorrectAnswerTime = 0;
        question.firstSolverAddress = address(0);
        // เคลียร์ Pool Solvers หากมี (สำหรับ Pool Mode)
        question.poolCorrectSolvers = new address[](0);
        // blockCreationTime และ mode ไม่ได้รีเซ็ต เพราะเป็นส่วนหนึ่งของคำถามที่ถูกสร้างขึ้นมาแล้ว
        // lastAnswerDay (mapping) จะถูกรีเซ็ตโดยอัตโนมัติเมื่อผู้เล่นตอบในวันใหม่

        emit QuestionActivatedFromBank(_questionId, msg.sender);
    }

    // 🚪 NEW: Leaf Management Functions
    
    /// @notice Register a leaf as belonging to a question (for tracking)
    function registerLeaf(uint256 _questionId, bytes32 _answerLeaf) public onlyAdmin {
        LibAppStorage.AppStorage storage ds = _appStorage();
        require(ds.questions[_questionId].correctAnswerHash != bytes32(0), "Quiz: Question does not exist.");
        
        ds.leafQuestionId[_answerLeaf] = _questionId;
        emit LeafRegistered(_questionId, _answerLeaf);
    }

    /// @notice Check if a specific leaf (quiz) is solved
    function isLeafSolved(bytes32 _answerLeaf) public view returns (bool) {
        return LibAppStorage.s().leafSolved[_answerLeaf];
    }

    /// @notice Get who solved a specific leaf
    function getLeafSolver(bytes32 _answerLeaf) public view returns (address) {
        return LibAppStorage.s().leafSolver[_answerLeaf];
    }

    /// @notice Get when a leaf was solved
    function getLeafSolveTime(bytes32 _answerLeaf) public view returns (uint256) {
        return LibAppStorage.s().leafSolveTime[_answerLeaf];
    }

    /// @notice Get which question a leaf belongs to
    function getLeafQuestionId(bytes32 _answerLeaf) public view returns (uint256) {
        return LibAppStorage.s().leafQuestionId[_answerLeaf];
    }

    /// @notice Admin function to reset a leaf (reopen a quiz)
    function resetLeaf(bytes32 _answerLeaf) public onlyAdmin {
        LibAppStorage.AppStorage storage ds = _appStorage();
        ds.leafSolved[_answerLeaf] = false;
        ds.leafSolver[_answerLeaf] = address(0);
        ds.leafSolveTime[_answerLeaf] = 0;
        // Keep leafQuestionId for tracking
    }
}

contracts/facets/QuizGameRewardFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { LibAppStorage } from '../libraries/LibAppStorage.sol';
import { IPoolManager } from '../interfaces/IPoolManager.sol';
import { IQuizCoin } from '../interfaces/IQuizCoin.sol';

/**
 * @title QuizGameRewardFacet
 * @dev Facet สำหรับจัดการการคำนวณและแจกจ่ายรางวัล
 */
contract QuizGameRewardFacet {
    
    /// @notice คำนวณรางวัลปัจจุบันตามระดับความยากและกลไก Halving
    /// @param _difficultyLevel ระดับความยากของคำถาม
    /// @return uint256 จำนวนรางวัลที่ควรได้รับ
    function calculateCurrentReward(uint256 _difficultyLevel) external view returns (uint256) {
        LibAppStorage.AppStorage storage ds = LibAppStorage.s();
        
        if (_difficultyLevel == 100) {
            return ds.REWARD_FOR_LEVEL_100;
        }

        require(_difficultyLevel >= 1 && _difficultyLevel <= 99, "Quiz: Invalid question level for halving calculation");

        uint256 currentBaseReward = (ds.REWARD_FOR_LEVEL_1_99 * _difficultyLevel) / 99;

        uint256 timeElapsed = block.timestamp - ds.GAME_START_TIMESTAMP;
        uint256 halvingCycles = timeElapsed / ds.HALVING_PERIOD_SECONDS;

        uint256 finalReward = currentBaseReward;
        for (uint256 i = 0; i < halvingCycles; i++) {
            finalReward = finalReward / 2;
            if (finalReward < ds.MIN_REWARD_AFTER_HALVING) {
                finalReward = ds.MIN_REWARD_AFTER_HALVING;
                break;
            }
        }
        return finalReward;
    }
}

contracts/facets/QuizParticipationFacet..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { LibAppStorage } from '../libraries/LibAppStorage.sol';
import { AccessControlUpgradeable } from '@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol';
import { IQuizGameEvents } from '../interfaces/IQuizGameEvents.sol';
import { Question, Quiz } from '../QuizTypes.sol';
import { IMerkleFacet } from "../interfaces/IMerkleFacet.sol";

/**
 * @title QuizParticipationFacet
 * @dev Facet ที่จัดการ Logic การเข้าร่วมเกมควิซ
 */
contract QuizParticipationFacet is IQuizGameEvents {

    function _appStorage() internal pure returns (LibAppStorage.AppStorage storage ds) {
        ds = LibAppStorage.s();
    }

    modifier onlyCreator(uint256 _quizId) {
        LibAppStorage.AppStorage storage ds = _appStorage();
        require(
            ds.quizzes[_quizId].creator == msg.sender,
            "AccessControl: Caller is not the quiz creator"
        );
        _;
    }

    /// @notice ผู้เล่นเข้าร่วมเกมควิซ
    function joinQuiz(uint256 _quizId) public {
        LibAppStorage.AppStorage storage ds = _appStorage();
        Quiz storage quiz = ds.quizzes[_quizId];

        require(quiz.isActive, "Quiz: Quiz is not active.");
        require(ds.quizParticipations[_quizId][msg.sender].hasJoined == false, "Quiz: You have already joined this quiz.");
        require(ds.quizPlayers[_quizId].length < quiz.maxParticipants, "Quiz: The quiz is full.");

        ds.quizParticipations[_quizId][msg.sender].hasJoined = true;
        ds.quizPlayers[_quizId].push(msg.sender);

        emit PlayerJoinedQuiz(msg.sender, _quizId);
    }

    /// @notice ผู้เล่นส่งคำตอบสำหรับคำถามในเกมควิซ
    function submitQuizAnswer(
        uint256 _quizId,
        uint256 _questionIndex,
        uint8 _submittedAnswerIndex,
        bytes32 leaf,
        bytes32[] calldata proof
    ) public {
        LibAppStorage.AppStorage storage ds = _appStorage();
        LibAppStorage.PlayerParticipation storage player = ds.quizParticipations[_quizId][msg.sender];
        Quiz storage quiz = ds.quizzes[_quizId];

        require(player.hasJoined, "Quiz: You must join the quiz first.");
        require(quiz.isActive, "Quiz: Quiz is not active.");
        require(_questionIndex < quiz.totalQuestions, "Quiz: Question index out of bounds.");
        require(player.lastAnsweredQuestionIndex < _questionIndex, "Quiz: You have already answered a higher or equal index question.");

        // ✅ Verify ผ่าน MerkleFacet
        bool verified = IMerkleFacet(address(this)).verifyQuiz(_quizId, leaf, proof);

        require(verified, "Quiz: Invalid answer proof.");

        player.lastAnsweredQuestionIndex = _questionIndex;
        player.playerAnswers[_questionIndex] = _submittedAnswerIndex;
        player.hasAnswered[_questionIndex] = true;
        player.score++;

        emit QuizAnswerSubmitted(msg.sender, _quizId, _questionIndex, _submittedAnswerIndex, true);
    }
}

